import OpenAI from "openai";

// the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
const openai = new OpenAI({ 
  apiKey: process.env.OPENAI_API_KEY || process.env.OPENAI_API_KEY_ENV_VAR || "default_key" 
});

export async function generateEventRecommendations(userProfile: {
  skills: string[];
  interests: string[];
  pastEvents: string[];
}): Promise<{
  recommendations: Array<{
    type: string;
    score: number;
    reason: string;
    suggestedTags: string[];
  }>;
}> {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [
        {
          role: "system",
          content: "You are an AI event recommendation engine. Analyze user profiles and suggest relevant event types, team matches, and learning opportunities. Respond with JSON in this format: { 'recommendations': [{ 'type': string, 'score': number, 'reason': string, 'suggestedTags': string[] }] }",
        },
        {
          role: "user",
          content: `User profile: Skills: ${userProfile.skills.join(', ')}, Interests: ${userProfile.interests.join(', ')}, Past events: ${userProfile.pastEvents.join(', ')}. Generate 5 personalized recommendations.`,
        },
      ],
      response_format: { type: "json_object" },
    });

    const result = JSON.parse(response.choices[0].message.content || "{}");
    return result;
  } catch (error) {
    console.error("OpenAI recommendation error:", error);
    throw new Error("Failed to generate AI recommendations");
  }
}

export async function generateTeamMatches(userProfile: {
  skills: string[];
  interests: string[];
  workStyle: string;
}): Promise<{
  matches: Array<{
    compatibility: number;
    reason: string;
    recommendedRoles: string[];
  }>;
}> {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [
        {
          role: "system",
          content: "You are an AI team matching system. Analyze user profiles and suggest team compatibility. Respond with JSON in this format: { 'matches': [{ 'compatibility': number, 'reason': string, 'recommendedRoles': string[] }] }",
        },
        {
          role: "user",
          content: `User profile: Skills: ${userProfile.skills.join(', ')}, Interests: ${userProfile.interests.join(', ')}, Work style: ${userProfile.workStyle}. Find compatible team members.`,
        },
      ],
      response_format: { type: "json_object" },
    });

    const result = JSON.parse(response.choices[0].message.content || "{}");
    return result;
  } catch (error) {
    console.error("OpenAI team matching error:", error);
    throw new Error("Failed to generate team matches");
  }
}

export async function analyzeImage(base64Image: string): Promise<string> {
  try {
    const visionResponse = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [
        {
          role: "user",
          content: [
            {
              type: "text",
              text: "Analyze this image and describe its key elements, context, and any notable aspects relevant to event management or team collaboration."
            },
            {
              type: "image_url",
              image_url: {
                url: `data:image/jpeg;base64,${base64Image}`
              }
            }
          ],
        },
      ],
      max_tokens: 500,
    });

    return visionResponse.choices[0].message.content || "";
  } catch (error) {
    console.error("OpenAI image analysis error:", error);
    throw new Error("Failed to analyze image");
  }
}

export async function analyzeSentiment(text: string): Promise<{
  rating: number;
  confidence: number;
  insights: string;
}> {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [
        {
          role: "system",
          content: "You are a sentiment analysis expert. Analyze the sentiment of the text and provide a rating from 1 to 5 stars, a confidence score between 0 and 1, and insights. Respond with JSON in this format: { 'rating': number, 'confidence': number, 'insights': string }",
        },
        {
          role: "user",
          content: text,
        },
      ],
      response_format: { type: "json_object" },
    });

    const result = JSON.parse(response.choices[0].message.content || "{}");

    return {
      rating: Math.max(1, Math.min(5, Math.round(result.rating))),
      confidence: Math.max(0, Math.min(1, result.confidence)),
      insights: result.insights || "No insights available",
    };
  } catch (error) {
    console.error("OpenAI sentiment analysis error:", error);
    throw new Error("Failed to analyze sentiment");
  }
}

export async function generateEventContent(eventType: string, theme: string): Promise<{
  title: string;
  description: string;
  suggestedSchedule: Array<{
    time: string;
    activity: string;
    duration: string;
  }>;
  requirements: string[];
  tags: string[];
}> {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [
        {
          role: "system",
          content: "You are an AI event planning assistant. Generate comprehensive event content including title, description, schedule, requirements, and tags. Respond with JSON in this format: { 'title': string, 'description': string, 'suggestedSchedule': [{ 'time': string, 'activity': string, 'duration': string }], 'requirements': string[], 'tags': string[] }",
        },
        {
          role: "user",
          content: `Generate content for a ${eventType} event with theme: ${theme}. Include detailed schedule, requirements, and relevant tags.`,
        },
      ],
      response_format: { type: "json_object" },
    });

    const result = JSON.parse(response.choices[0].message.content || "{}");
    return result;
  } catch (error) {
    console.error("OpenAI content generation error:", error);
    throw new Error("Failed to generate event content");
  }
}

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET!,
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: true,
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      res.redirect(
        client.buildEndSessionUrl(config, {
          client_id: process.env.REPL_ID!,
          post_logout_redirect_uri: `${req.protocol}://${req.hostname}`,
        }).href
      );
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer, WebSocket } from "ws";
import { storage } from "./storage";
import { setupAuth, isAuthenticated } from "./replitAuth";
import { insertEventSchema, insertTeamSchema, insertChatMessageSchema } from "@shared/schema";
import { z } from "zod";
import Stripe from "stripe";
import { analyzeImage, analyzeSentiment } from "./openai";
import { sendEmail } from "./sendgrid";
import multer from "multer";

// Initialize Stripe
const stripe = process.env.STRIPE_SECRET_KEY ? new Stripe(process.env.STRIPE_SECRET_KEY, {
  apiVersion: "2025-08-27.basil",
}) : null;

// Prototype mode flag - when Stripe keys are not available, use mock payments
const isPrototypeMode = !process.env.STRIPE_SECRET_KEY;

// Configure multer for file uploads
const upload = multer({
  limits: { fileSize: 10 * 1024 * 1024 }, // 10MB limit
  fileFilter: (req, file, cb) => {
    // Allow images and common file types
    const allowedTypes = ['image/jpeg', 'image/png', 'image/gif', 'application/pdf', 'text/plain'];
    cb(null, allowedTypes.includes(file.mimetype));
  }
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Auth middleware
  await setupAuth(app);

  // Auth routes
  app.get('/api/auth/user', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  // Event routes
  app.get('/api/events', async (req, res) => {
    try {
      const { type, status, search } = req.query;
      const events = await storage.getEvents({
        type: type as string,
        status: status as string,
        search: search as string,
      });
      res.json(events);
    } catch (error) {
      console.error("Error fetching events:", error);
      res.status(500).json({ message: "Failed to fetch events" });
    }
  });

  app.get('/api/events/:id', async (req, res) => {
    try {
      const event = await storage.getEvent(req.params.id);
      if (!event) {
        return res.status(404).json({ message: "Event not found" });
      }
      res.json(event);
    } catch (error) {
      console.error("Error fetching event:", error);
      res.status(500).json({ message: "Failed to fetch event" });
    }
  });

  app.post('/api/events', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const eventData = insertEventSchema.parse({
        ...req.body,
        organizerId: userId,
      });

      const event = await storage.createEvent(eventData);

      // Record analytics
      await storage.recordEventMetric(event.id, 'created', 1);

      res.json(event);
    } catch (error) {
      console.error("Error creating event:", error);
      res.status(500).json({ message: "Failed to create event" });
    }
  });

  app.put('/api/events/:id', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const event = await storage.getEvent(req.params.id);

      if (!event || event.organizerId !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      const updates = insertEventSchema.partial().parse(req.body);
      const updatedEvent = await storage.updateEvent(req.params.id, updates);

      res.json(updatedEvent);
    } catch (error) {
      console.error("Error updating event:", error);
      res.status(500).json({ message: "Failed to update event" });
    }
  });

  // Event registration routes
  app.post('/api/events/:id/register', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { teamId } = req.body;

      const registration = await storage.registerForEvent(req.params.id, userId, teamId);

      // Record analytics
      await storage.recordEventMetric(req.params.id, 'registrations', 1);

      // Send confirmation email
      const user = await storage.getUser(userId);
      const event = await storage.getEvent(req.params.id);

      if (user?.email && event) {
        await sendEmail({
          to: user.email,
          from: process.env.FROM_EMAIL || 'noreply@nexus.dev',
          subject: `Registration confirmed for ${event.title}`,
          text: `You have successfully registered for ${event.title}. Event starts on ${event.startDate}.`,
        });
      }

      res.json(registration);
    } catch (error) {
      console.error("Error registering for event:", error);
      res.status(500).json({ message: "Failed to register for event" });
    }
  });

  app.get('/api/events/:id/registrations', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const event = await storage.getEvent(req.params.id);

      if (!event || event.organizerId !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      const registrations = await storage.getEventRegistrations(req.params.id);
      res.json(registrations);
    } catch (error) {
      console.error("Error fetching registrations:", error);
      res.status(500).json({ message: "Failed to fetch registrations" });
    }
  });

  // Team routes
  app.post('/api/teams', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const teamData = insertTeamSchema.parse({
        ...req.body,
        leaderId: userId,
      });

      const team = await storage.createTeam(teamData);
      res.json(team);
    } catch (error) {
      console.error("Error creating team:", error);
      res.status(500).json({ message: "Failed to create team" });
    }
  });

  app.post('/api/teams/:id/join', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      await storage.joinTeam(req.params.id, userId);
      res.json({ message: "Joined team successfully" });
    } catch (error) {
      console.error("Error joining team:", error);
      res.status(500).json({ message: "Failed to join team" });
    }
  });

  app.get('/api/events/:id/teams', async (req, res) => {
    try {
      const teams = await storage.getEventTeams(req.params.id);
      res.json(teams);
    } catch (error) {
      console.error("Error fetching teams:", error);
      res.status(500).json({ message: "Failed to fetch teams" });
    }
  });

  // Chat routes
  app.get('/api/events/:id/chat', isAuthenticated, async (req, res) => {
    try {
      const messages = await storage.getEventChatMessages(req.params.id, 50);
      res.json(messages);
    } catch (error) {
      console.error("Error fetching chat messages:", error);
      res.status(500).json({ message: "Failed to fetch chat messages" });
    }
  });

  app.post('/api/events/:id/chat', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const messageData = insertChatMessageSchema.parse({
        ...req.body,
        eventId: req.params.id,
        userId,
      });

      const message = await storage.createChatMessage(messageData);

      // Broadcast to WebSocket clients
      wss.clients.forEach((client) => {
        if (client.readyState === WebSocket.OPEN) {
          client.send(JSON.stringify({
            type: 'chat_message',
            eventId: req.params.id,
            message,
          }));
        }
      });

      res.json(message);
    } catch (error) {
      console.error("Error sending chat message:", error);
      res.status(500).json({ message: "Failed to send message" });
    }
  });

  // AI routes
  app.get('/api/ai/recommendations', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const { type } = req.query;

      const recommendations = await storage.getUserRecommendations(userId, type as string);
      res.json(recommendations);
    } catch (error) {
      console.error("Error fetching AI recommendations:", error);
      res.status(500).json({ message: "Failed to fetch recommendations" });
    }
  });

  app.post('/api/ai/generate-recommendations', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const user = await storage.getUser(userId);

      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }

      // Get user's skills and interests for AI recommendations
      const userSkills = user.skills || [];
      const userInterests = user.interests || [];

      // Find recommended events based on user profile
      const events = await storage.getEvents({ status: 'published' });

      for (const event of events.slice(0, 5)) {
        // Simple matching algorithm - in production, use more sophisticated AI
        const skillsMatch = event.tags?.some(tag => userSkills.includes(tag)) || false;
        const interestsMatch = event.tags?.some(tag => userInterests.includes(tag)) || false;

        if (skillsMatch || interestsMatch) {
          const score = (skillsMatch ? 0.5 : 0) + (interestsMatch ? 0.5 : 0);
          await storage.createAIRecommendation(
            userId,
            'event',
            event.id,
            score,
            `Matches your ${skillsMatch ? 'skills' : 'interests'}`
          );
        }
      }

      // Find potential teammates
      if (userSkills.length > 0) {
        const teammates = await storage.findTeammates(userId, userSkills);

        for (const teammate of teammates.slice(0, 3)) {
          await storage.createAIRecommendation(
            userId,
            'team',
            teammate.id,
            0.8,
            'Complementary skills for collaboration'
          );
        }
      }

      res.json({ message: "AI recommendations generated successfully" });
    } catch (error) {
      console.error("Error generating AI recommendations:", error);
      res.status(500).json({ message: "Failed to generate recommendations" });
    }
  });

  // Analytics routes
  app.get('/api/events/:id/analytics', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const event = await storage.getEvent(req.params.id);

      if (!event || event.organizerId !== userId) {
        return res.status(403).json({ message: "Access denied" });
      }

      const analytics = await storage.getEventAnalytics(req.params.id);
      res.json(analytics);
    } catch (error) {
      console.error("Error fetching analytics:", error);
      res.status(500).json({ message: "Failed to fetch analytics" });
    }
  });

  app.get('/api/dashboard/stats', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;

      // Get user's organized events
      const organizedEvents = await storage.getEventsByOrganizer(userId);

      // Get user's registered events
      const userEvents = await storage.getUserEvents(userId);

      // Get user's notifications
      const notifications = await storage.getUserNotifications(userId, true);

      const stats = {
        organizedEvents: organizedEvents.length,
        registeredEvents: userEvents.length,
        unreadNotifications: notifications.length,
        totalParticipants: 0, // TODO: Calculate from registrations
      };

      res.json(stats);
    } catch (error) {
      console.error("Error fetching dashboard stats:", error);
      res.status(500).json({ message: "Failed to fetch dashboard stats" });
    }
  });

  // Notification routes
  app.get('/api/notifications', isAuthenticated, async (req: any, res) => {
    try {
      const userId = req.user.claims.sub;
      const notifications = await storage.getUserNotifications(userId);
      res.json(notifications);
    } catch (error) {
      console.error("Error fetching notifications:", error);
      res.status(500).json({ message: "Failed to fetch notifications" });
    }
  });

  app.put('/api/notifications/:id/read', isAuthenticated, async (req, res) => {
    try {
      await storage.markNotificationRead(req.params.id);
      res.json({ message: "Notification marked as read" });
    } catch (error) {
      console.error("Error marking notification as read:", error);
      res.status(500).json({ message: "Failed to mark notification as read" });
    }
  });

  // Payment routes (Stripe with prototype mode support)
  if (stripe) {
    console.log("ðŸ”µ Stripe configured - using real payment processing");
    app.post("/api/create-payment-intent", isAuthenticated, async (req, res) => {
      try {
        const { amount, eventId } = req.body;

        const paymentIntent = await stripe.paymentIntents.create({
          amount: Math.round(amount * 100), // Convert to cents
          currency: "usd",
          metadata: { eventId },
        });

        res.json({ clientSecret: paymentIntent.client_secret });
      } catch (error: any) {
        console.error("Error creating payment intent:", error);
        res.status(500).json({ message: "Error creating payment intent: " + error.message });
      }
    });

    app.post('/api/create-subscription', isAuthenticated, async (req: any, res) => {
      try {
        const userId = req.user.claims.sub;
        let user = await storage.getUser(userId);

        if (!user) {
          return res.status(404).json({ message: "User not found" });
        }

        if (user.stripeSubscriptionId) {
          const subscription = await stripe.subscriptions.retrieve(user.stripeSubscriptionId);
          res.json({
            subscriptionId: subscription.id,
            clientSecret: (subscription.latest_invoice as any)?.payment_intent?.client_secret,
          });
          return;
        }

        if (!user.email) {
          return res.status(400).json({ message: 'No user email on file' });
        }

        const customer = await stripe.customers.create({
          email: user.email,
          name: `${user.firstName} ${user.lastName}`.trim(),
        });

        user = await storage.updateUserStripeInfo(user.id, customer.id);

        const subscription = await stripe.subscriptions.create({
          customer: customer.id,
          items: [{
            price: process.env.STRIPE_PRICE_ID || 'price_1234567890', // Default price ID
          }],
          payment_behavior: 'default_incomplete',
          expand: ['latest_invoice.payment_intent'],
        });

        await storage.updateUserStripeInfo(user.id, customer.id, subscription.id);

        res.json({
          subscriptionId: subscription.id,
          clientSecret: (subscription.latest_invoice as any)?.payment_intent?.client_secret,
        });
      } catch (error: any) {
        console.error("Error creating subscription:", error);
        res.status(400).json({ error: { message: error.message } });
      }
    });
  } else if (isPrototypeMode) {
    console.log("ðŸŸ¡ PROTOTYPE MODE - Using mock payment processing (no real charges)");

    // Mock payment intent creation
    app.post("/api/create-payment-intent", isAuthenticated, async (req, res) => {
      try {
        const { amount, eventId } = req.body;

        // Generate a mock client secret for prototype mode
        const mockClientSecret = `pi_mock_${Date.now()}_secret_${Math.random().toString(36).substr(2, 9)}`;

        console.log(`ðŸ’° PROTOTYPE: Mock payment intent created for $${amount} (Event: ${eventId})`);

        res.json({ 
          clientSecret: mockClientSecret,
          prototypeModeWarning: "This is a prototype payment - no real charges will be made"
        });
      } catch (error: any) {
        console.error("Error creating mock payment intent:", error);
        res.status(500).json({ message: "Error creating mock payment intent: " + error.message });
      }
    });

    // Mock subscription creation
    app.post('/api/create-subscription', isAuthenticated, async (req: any, res) => {
      try {
        const userId = req.user.claims.sub;
        const user = await storage.getUser(userId);

        if (!user) {
          return res.status(404).json({ message: "User not found" });
        }

        // Generate mock subscription data
        const mockSubscriptionId = `sub_mock_${Date.now()}`;
        const mockClientSecret = `pi_mock_${Date.now()}_secret_${Math.random().toString(36).substr(2, 9)}`;

        console.log(`ðŸ“± PROTOTYPE: Mock subscription created for user ${userId}`);

        res.json({
          subscriptionId: mockSubscriptionId,
          clientSecret: mockClientSecret,
          prototypeModeWarning: "This is a prototype subscription - no real charges will be made"
        });
      } catch (error: any) {
        console.error("Error creating mock subscription:", error);
        res.status(400).json({ error: { message: error.message } });
      }
    });
  }

  // File upload routes
  app.post('/api/upload', isAuthenticated, upload.single('file'), async (req: any, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ message: "No file uploaded" });
      }

      // In production, upload to cloud storage (AWS S3, etc.)
      // For now, return a mock URL
      const fileUrl = `/uploads/${Date.now()}-${req.file.originalname}`;

      res.json({ 
        url: fileUrl,
        filename: req.file.originalname,
        size: req.file.size,
        mimetype: req.file.mimetype
      });
    } catch (error) {
      console.error("Error uploading file:", error);
      res.status(500).json({ message: "Failed to upload file" });
    }
  });

  // Search routes
  app.get('/api/search', async (req, res) => {
    try {
      const { q } = req.query;
      if (!q || typeof q !== 'string') {
        return res.json([]);
      }

      const events = await storage.searchEvents(q);
      res.json(events);
    } catch (error) {
      console.error("Error searching events:", error);
      res.status(500).json({ message: "Failed to search events" });
    }
  });

  const httpServer = createServer(app);

  // WebSocket server for real-time features
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  wss.on('connection', (ws) => {
    console.log('New WebSocket connection');

    ws.on('message', async (data) => {
      try {
        const message = JSON.parse(data.toString());

        // Handle different message types
        switch (message.type) {
          case 'join_event':
            // Join event room for real-time updates
            ws.eventId = message.eventId;
            break;
          case 'chat_message':
            // Broadcast chat message to all clients in the same event
            wss.clients.forEach((client) => {
              if (client !== ws && client.readyState === WebSocket.OPEN && client.eventId === message.eventId) {
                client.send(JSON.stringify(message));
              }
            });
            break;
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
      }
    });

    ws.on('close', () => {
      console.log('WebSocket connection closed');
    });
  });

  return httpServer;
}

