import OpenAI from "openai";

if (!process.env.OPENAI_API_KEY) {
  throw new Error("OPENAI_API_KEY environment variable must be set");
}

// the newest OpenAI model is "gpt-5" which was released August 7, 2025. do not change this unless explicitly requested by the user
const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });

export interface TeamMatchingRequest {
  userId: string;
  skills: string[];
  interests: string[];
  eventType: string;
  teamSize?: number;
}

export interface TeamMatchingResult {
  recommendedUsers: {
    userId: string;
    matchScore: number;
    reasoning: string;
    complementarySkills: string[];
  }[];
  suggestedTeamComposition: {
    roles: string[];
    skillGaps: string[];
    recommendedSize: number;
  };
}

export interface EventRecommendationRequest {
  userId: string;
  skills: string[];
  interests: string[];
  pastEvents: string[];
  careerGoals?: string;
}

export interface EventRecommendationResult {
  recommendedEvents: {
    eventId: string;
    matchScore: number;
    reasoning: string;
    learningOutcomes: string[];
    careerBenefits: string[];
  }[];
  skillDevelopmentSuggestions: {
    skill: string;
    currentLevel: string;
    targetLevel: string;
    recommendedPath: string[];
  }[];
}

export async function generateTeamMatches(
  request: TeamMatchingRequest,
  availableUsers: Array<{ id: string; skills: string[]; interests: string[]; bio?: string }>
): Promise<TeamMatchingResult> {
  try {
    const prompt = `
You are an AI team formation expert. Analyze the following user profile and available team members to suggest optimal team matches.

Target User:
- Skills: ${request.skills.join(', ')}
- Interests: ${request.interests.join(', ')}
- Event Type: ${request.eventType}
- Preferred Team Size: ${request.teamSize || 4}

Available Users:
${availableUsers.map(user => `
- ID: ${user.id}
- Skills: ${user.skills.join(', ')}
- Interests: ${user.interests.join(', ')}
- Bio: ${user.bio || 'No bio provided'}
`).join('\n')}

Provide team matching recommendations with match scores (0-100) and detailed reasoning. 
Also suggest optimal team composition and identify any skill gaps.

Respond with JSON in this exact format:
{
  "recommendedUsers": [
    {
      "userId": "user_id",
      "matchScore": 85,
      "reasoning": "Strong complementary skills in backend development",
      "complementarySkills": ["Python", "DevOps"]
    }
  ],
  "suggestedTeamComposition": {
    "roles": ["Frontend Lead", "Backend Developer", "UI/UX Designer", "Data Scientist"],
    "skillGaps": ["Machine Learning", "Cloud Infrastructure"],
    "recommendedSize": 4
  }
}`;

    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [{ role: "user", content: prompt }],
      response_format: { type: "json_object" },
    });

    const result = JSON.parse(response.choices[0].message.content || '{}');
    return result as TeamMatchingResult;
  } catch (error) {
    console.error("Error generating team matches:", error);
    throw new Error("Failed to generate team matches");
  }
}

export async function generateEventRecommendations(
  request: EventRecommendationRequest,
  availableEvents: Array<{ id: string; title: string; type: string; description: string; tags: string[] }>
): Promise<EventRecommendationResult> {
  try {
    const prompt = `
You are an AI career development and event recommendation expert. Analyze the user profile and suggest the most relevant events.

User Profile:
- Skills: ${request.skills.join(', ')}
- Interests: ${request.interests.join(', ')}
- Past Events: ${request.pastEvents.join(', ')}
- Career Goals: ${request.careerGoals || 'Not specified'}

Available Events:
${availableEvents.map(event => `
- ID: ${event.id}
- Title: ${event.title}
- Type: ${event.type}
- Description: ${event.description}
- Tags: ${event.tags.join(', ')}
`).join('\n')}

Provide personalized event recommendations with match scores (0-100) and explain how each event aligns with their goals.
Also suggest skill development paths based on their current skills and career aspirations.

Respond with JSON in this exact format:
{
  "recommendedEvents": [
    {
      "eventId": "event_id",
      "matchScore": 92,
      "reasoning": "Perfect alignment with your React skills and career goals",
      "learningOutcomes": ["Advanced React patterns", "Performance optimization"],
      "careerBenefits": ["Senior developer role preparation", "Industry networking"]
    }
  ],
  "skillDevelopmentSuggestions": [
    {
      "skill": "Machine Learning",
      "currentLevel": "Beginner",
      "targetLevel": "Intermediate",
      "recommendedPath": ["Complete Python ML course", "Build 2-3 ML projects", "Attend ML workshops"]
    }
  ]
}`;

    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [{ role: "user", content: prompt }],
      response_format: { type: "json_object" },
    });

    const result = JSON.parse(response.choices[0].message.content || '{}');
    return result as EventRecommendationResult;
  } catch (error) {
    console.error("Error generating event recommendations:", error);
    throw new Error("Failed to generate event recommendations");
  }
}

export async function generateEventContent(eventType: string, topic: string, duration: string): Promise<{
  title: string;
  description: string;
  agenda: Array<{ time: string; activity: string; description: string }>;
  requirements: string[];
  learningObjectives: string[];
}> {
  try {
    const prompt = `
Generate comprehensive content for a ${eventType} on ${topic} lasting ${duration}.

Create a detailed event plan including:
1. Compelling title
2. Engaging description
3. Structured agenda with time slots
4. Prerequisites and requirements
5. Clear learning objectives

The content should be professional, engaging, and appropriate for the target audience.

Respond with JSON in this exact format:
{
  "title": "Event title",
  "description": "Detailed description",
  "agenda": [
    {
      "time": "9:00-9:30 AM",
      "activity": "Welcome & Introductions",
      "description": "Getting to know participants and setting expectations"
    }
  ],
  "requirements": ["Basic programming knowledge", "Laptop with development environment"],
  "learningObjectives": ["Understand core concepts", "Build practical projects"]
}`;

    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [{ role: "user", content: prompt }],
      response_format: { type: "json_object" },
    });

    const result = JSON.parse(response.choices[0].message.content || '{}');
    return result;
  } catch (error) {
    console.error("Error generating event content:", error);
    throw new Error("Failed to generate event content");
  }
}

export async function analyzeEventFeedback(feedback: Array<{ rating: number; comment: string }>): Promise<{
  overallSentiment: string;
  keyThemes: string[];
  improvementSuggestions: string[];
  strengthsIdentified: string[];
  averageRating: number;
}> {
  try {
    const prompt = `
Analyze the following event feedback to provide insights and recommendations:

Feedback Data:
${feedback.map((f, i) => `
Review ${i + 1}:
- Rating: ${f.rating}/5
- Comment: "${f.comment}"
`).join('\n')}

Provide a comprehensive analysis including:
1. Overall sentiment analysis
2. Key themes and patterns
3. Specific improvement suggestions
4. Identified strengths
5. Statistical summary

Respond with JSON in this exact format:
{
  "overallSentiment": "positive/neutral/negative",
  "keyThemes": ["Theme 1", "Theme 2"],
  "improvementSuggestions": ["Suggestion 1", "Suggestion 2"],
  "strengthsIdentified": ["Strength 1", "Strength 2"],
  "averageRating": 4.2
}`;

    const response = await openai.chat.completions.create({
      model: "gpt-5",
      messages: [{ role: "user", content: prompt }],
      response_format: { type: "json_object" },
    });

    const result = JSON.parse(response.choices[0].message.content || '{}');
    return result;
  } catch (error) {
    console.error("Error analyzing feedback:", error);
    throw new Error("Failed to analyze event feedback");
  }
}

