import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Brain, RefreshCw, Code, Users, Trophy } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface AIRecommendation {
  id: string;
  type: string;
  entityId: string;
  score: string;
  reason: string;
  createdAt: string;
}

export default function AIRecommendations() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: recommendations, isLoading } = useQuery<AIRecommendation[]>({
    queryKey: ["/api/ai/recommendations"],
  });

  const generateRecommendations = useMutation({
    mutationFn: async () => {
      await apiRequest("POST", "/api/ai/generate-recommendations");
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/ai/recommendations"] });
      toast({
        title: "Success",
        description: "AI recommendations generated successfully!",
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const getRecommendationIcon = (type: string) => {
    switch (type) {
      case 'event':
        return <Code className="h-4 w-4" />;
      case 'team':
        return <Users className="h-4 w-4" />;
      default:
        return <Trophy className="h-4 w-4" />;
    }
  };

  const getRecommendationColor = (type: string) => {
    switch (type) {
      case 'event':
        return 'bg-primary/20 text-primary';
      case 'team':
        return 'bg-accent/20 text-accent';
      default:
        return 'bg-chart-4/20 text-chart-4';
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Brain className="h-5 w-5 text-accent animate-pulse" />
            AI Insights
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="p-4 rounded-lg bg-muted animate-pulse">
                <div className="h-4 bg-muted-foreground/20 rounded w-3/4 mb-2" />
                <div className="h-3 bg-muted-foreground/20 rounded w-1/2" />
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader className="flex flex-row items-center justify-between space-y-0">
        <CardTitle className="flex items-center gap-2">
          <Brain className="h-5 w-5 text-accent" />
          AI Insights
        </CardTitle>
        <Button
          variant="ghost"
          size="sm"
          onClick={() => generateRecommendations.mutate()}
          disabled={generateRecommendations.isPending}
        >
          <RefreshCw className={`h-4 w-4 ${generateRecommendations.isPending ? 'animate-spin' : ''}`} />
        </Button>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {recommendations?.length === 0 ? (
            <div className="text-center py-8">
              <Brain className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
              <p className="text-muted-foreground mb-4">No AI recommendations yet</p>
              <Button
                onClick={() => generateRecommendations.mutate()}
                disabled={generateRecommendations.isPending}
                className="bg-accent text-accent-foreground hover:bg-accent/90"
              >
                {generateRecommendations.isPending ? (
                  <RefreshCw className="h-4 w-4 animate-spin mr-2" />
                ) : (
                  <Brain className="h-4 w-4 mr-2" />
                )}
                Generate Recommendations
              </Button>
            </div>
          ) : (
            <>
              {recommendations?.slice(0, 3).map((recommendation) => (
                <div
                  key={recommendation.id}
                  className="p-4 rounded-lg border border-border hover:border-primary/50 transition-colors"
                >
                  <div className="flex items-center justify-between mb-2">
                    <Badge variant="secondary" className={getRecommendationColor(recommendation.type)}>
                      {getRecommendationIcon(recommendation.type)}
                      <span className="ml-1 capitalize">{recommendation.type}</span>
                    </Badge>
                    <Badge variant="outline">
                      {Math.round(parseFloat(recommendation.score) * 100)}% match
                    </Badge>
                  </div>
                  <p className="text-sm font-medium mb-1">{recommendation.reason}</p>
                  <p className="text-xs text-muted-foreground">
                    {new Date(recommendation.createdAt).toLocaleDateString()}
                  </p>
                </div>
              ))}

              {recommendations && recommendations.length > 3 && (
                <Button variant="outline" className="w-full">
                  View All Recommendations
                </Button>
              )}
            </>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/Chat.tsx.content:
import { useState, useEffect, useRef } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { 
  Send, 
  Paperclip, 
  Smile, 
  MoreVertical,
  Phone,
  Video,
  Users,
  Settings,
  Search,
  X,
  Download,
  Image,
  FileText
} from "lucide-react";

interface ChatMessage {
  id: string;
  userId: string;
  message: string;
  messageType: 'text' | 'file' | 'image';
  fileUrl?: string;
  createdAt: string;
  user?: {
    id: string;
    firstName?: string;
    lastName?: string;
    profileImageUrl?: string;
  };
}

interface ChatProps {
  roomId: string;
  eventId?: string;
  teamId?: string;
}

export default function Chat({ roomId, eventId, teamId }: ChatProps) {
  const [message, setMessage] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [showSearch, setShowSearch] = useState(false);
  const [websocket, setWebsocket] = useState<WebSocket | null>(null);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Fetch existing messages
  const { data: messages, isLoading } = useQuery<ChatMessage[]>({
    queryKey: eventId 
      ? ["/api/events", eventId, "chat"]
      : ["/api/teams", teamId, "chat"],
    enabled: !!(eventId || teamId),
  });

  // Send message mutation
  const sendMessageMutation = useMutation({
    mutationFn: async (messageData: { message: string; messageType?: string; fileUrl?: string }) => {
      const endpoint = eventId 
        ? `/api/events/${eventId}/chat`
        : `/api/teams/${teamId}/chat`;

      await apiRequest("POST", endpoint, messageData);
    },
    onSuccess: () => {
      setMessage("");
      queryClient.invalidateQueries({ 
        queryKey: eventId 
          ? ["/api/events", eventId, "chat"]
          : ["/api/teams", teamId, "chat"]
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // File upload mutation
  const uploadFileMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append('file', file);

      const response = await apiRequest("POST", "/api/upload", formData);
      return await response.json();
    },
    onSuccess: (data) => {
      sendMessageMutation.mutate({
        message: `Shared file: ${data.filename}`,
        messageType: data.mimetype.startsWith('image/') ? 'image' : 'file',
        fileUrl: data.url,
      });
    },
    onError: (error: Error) => {
      toast({
        title: "Upload Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // WebSocket connection
  useEffect(() => {
    const protocol = window.location.protocol === "https:" ? "wss:" : "ws:";
    const wsUrl = `${protocol}//${window.location.host}/ws`;

    const ws = new WebSocket(wsUrl);

    ws.onopen = () => {
      console.log('WebSocket connected');
      setWebsocket(ws);

      // Join the room
      ws.send(JSON.stringify({
        type: 'join_event',
        eventId: eventId || `team_${teamId}`,
      }));
    };

    ws.onmessage = (event) => {
      try {
        const data = JSON.parse(event.data);
        if (data.type === 'chat_message') {
          // Invalidate queries to fetch new messages
          queryClient.invalidateQueries({ 
            queryKey: eventId 
              ? ["/api/events", eventId, "chat"]
              : ["/api/teams", teamId, "chat"]
          });
        }
      } catch (error) {
        console.error('WebSocket message error:', error);
      }
    };

    ws.onclose = () => {
      console.log('WebSocket disconnected');
      setWebsocket(null);
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };

    return () => {
      if (ws.readyState === WebSocket.OPEN) {
        ws.close();
      }
    };
  }, [eventId, teamId, queryClient]);

  // Auto-scroll to bottom
  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSendMessage = () => {
    if (!message.trim()) return;

    sendMessageMutation.mutate({
      message: message.trim(),
      messageType: 'text',
    });
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      if (file.size > 10 * 1024 * 1024) { // 10MB limit
        toast({
          title: "File too large",
          description: "Please select a file smaller than 10MB",
          variant: "destructive",
        });
        return;
      }
      uploadFileMutation.mutate(file);
    }
  };

  const formatTime = (dateString: string) => {
    return new Date(dateString).toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getFileIcon = (messageType: string) => {
    switch (messageType) {
      case 'image':
        return <Image className="h-4 w-4" />;
      case 'file':
        return <FileText className="h-4 w-4" />;
      default:
        return null;
    }
  };

  const filteredMessages = messages?.filter(msg => 
    !searchQuery || 
    msg.message.toLowerCase().includes(searchQuery.toLowerCase()) ||
    msg.user?.firstName?.toLowerCase().includes(searchQuery.toLowerCase()) ||
    msg.user?.lastName?.toLowerCase().includes(searchQuery.toLowerCase())
  ) || [];

  if (isLoading) {
    return (
      <Card className="h-96">
        <CardContent className="h-full flex items-center justify-center">
          <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="h-96 flex flex-col">
      <CardHeader className="flex-shrink-0 pb-3">
        <div className="flex items-center justify-between">
          <CardTitle className="flex items-center gap-2">
            <Users className="h-5 w-5" />
            {eventId ? 'Event Chat' : 'Team Chat'}
            <Badge variant="secondary" className="ml-2">
              <div className="w-2 h-2 bg-chart-4 rounded-full mr-1 animate-pulse" />
              Live
            </Badge>
          </CardTitle>
          <div className="flex items-center gap-2">
            <Button 
              variant="ghost" 
              size="sm"
              onClick={() => setShowSearch(!showSearch)}
              data-testid="button-search-chat"
            >
              <Search className="h-4 w-4" />
            </Button>
            <Button variant="ghost" size="sm">
              <Phone className="h-4 w-4" />
            </Button>
            <Button variant="ghost" size="sm">
              <Video className="h-4 w-4" />
            </Button>
            <Button variant="ghost" size="sm">
              <MoreVertical className="h-4 w-4" />
            </Button>
          </div>
        </div>

        {showSearch && (
          <div className="flex items-center gap-2 mt-2">
            <Input
              placeholder="Search messages..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="flex-1"
              data-testid="input-search-messages"
            />
            <Button 
              variant="ghost" 
              size="sm" 
              onClick={() => {
                setShowSearch(false);
                setSearchQuery("");
              }}
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        )}
      </CardHeader>

      <CardContent className="flex-1 overflow-hidden p-0">
        {/* Messages Area */}
        <div className="h-full overflow-y-auto p-4 space-y-4 scrollbar-thin">
          {filteredMessages.length === 0 ? (
            <div className="text-center text-muted-foreground py-8">
              {searchQuery ? (
                <div>
                  <Search className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p>No messages found matching "{searchQuery}"</p>
                </div>
              ) : (
                <div>
                  <Users className="h-12 w-12 mx-auto mb-4 opacity-50" />
                  <p>Start a conversation</p>
                  <p className="text-sm">Send the first message to get things going!</p>
                </div>
              )}
            </div>
          ) : (
            filteredMessages.map((msg) => (
              <div
                key={msg.id}
                className={`flex items-start gap-3 ${
                  msg.userId === user?.id ? 'flex-row-reverse' : ''
                }`}
              >
                <Avatar className="w-8 h-8 flex-shrink-0">
                  <AvatarImage src={msg.user?.profileImageUrl} />
                  <AvatarFallback className="text-xs">
                    {msg.user?.firstName?.[0]}{msg.user?.lastName?.[0]}
                  </AvatarFallback>
                </Avatar>

                <div className={`flex-1 max-w-xs ${msg.userId === user?.id ? 'text-right' : ''}`}>
                  <div className="flex items-center gap-2 mb-1">
                    <span className="text-xs font-medium">
                      {msg.userId === user?.id 
                        ? 'You' 
                        : `${msg.user?.firstName || ''} ${msg.user?.lastName || ''}`.trim() || 'Anonymous'
                      }
                    </span>
                    <span className="text-xs text-muted-foreground">
                      {formatTime(msg.createdAt)}
                    </span>
                  </div>

                  <div className={`rounded-lg p-3 ${
                    msg.userId === user?.id 
                      ? 'bg-primary text-primary-foreground ml-8' 
                      : 'bg-muted mr-8'
                  }`}>
                    {msg.messageType === 'text' ? (
                      <p className="text-sm break-words">{msg.message}</p>
                    ) : (
                      <div className="flex items-center gap-2">
                        {getFileIcon(msg.messageType)}
                        <span className="text-sm">{msg.message}</span>
                        {msg.fileUrl && (
                          <Button variant="ghost" size="sm" className="h-6 w-6 p-0">
                            <Download className="h-3 w-3" />
                          </Button>
                        )}
                      </div>
                    )}
                  </div>
                </div>
              </div>
            ))
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Message Input */}
        <div className="border-t border-border p-4">
          <div className="flex items-center gap-2">
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleFileUpload}
              className="hidden"
              accept="image/*,.pdf,.doc,.docx,.txt"
            />

            <Button
              variant="ghost"
              size="sm"
              onClick={() => fileInputRef.current?.click()}
              disabled={uploadFileMutation.isPending}
              data-testid="button-attach-file"
            >
              {uploadFileMutation.isPending ? (
                <div className="animate-spin w-4 h-4 border-2 border-current border-t-transparent rounded-full" />
              ) : (
                <Paperclip className="h-4 w-4" />
              )}
            </Button>

            <Input
              placeholder="Type your message..."
              value={message}
              onChange={(e) => setMessage(e.target.value)}
              onKeyDown={handleKeyPress}
              className="flex-1"
              disabled={sendMessageMutation.isPending}
              data-testid="input-chat-message"
            />

            <Button variant="ghost" size="sm">
              <Smile className="h-4 w-4" />
            </Button>

            <Button
              onClick={handleSendMessage}
              disabled={!message.trim() || sendMessageMutation.isPending}
              className="bg-primary text-primary-foreground hover:bg-primary/90"
              data-testid="button-send-message"
            >
              {sendMessageMutation.isPending ? (
                <div className="animate-spin w-4 h-4 border-2 border-current border-t-transparent rounded-full" />
              ) : (
                <Send className="h-4 w-4" />
              )}
            </Button>
          </div>

          {websocket?.readyState === WebSocket.OPEN && (
            <div className="flex items-center gap-1 mt-2 text-xs text-chart-4">
              <div className="w-2 h-2 bg-chart-4 rounded-full animate-pulse" />
              Connected
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/ChatInterface.tsx.content:
import { useState, useRef, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { Badge } from '@/components/ui/badge';
import { Separator } from '@/components/ui/separator';
import { useAuth } from '@/hooks/useAuth';
import { WebSocketMessage } from '@/types';
import { Send, Paperclip, Smile, MoreHorizontal, Wifi, WifiOff } from 'lucide-react';

interface ChatInterfaceProps {
  title: string;
  channelType: string;
  channelId: string;
  messages: WebSocketMessage[];
  onSendMessage: (
    content: string,
    channelType: string,
    recipientId?: string,
    teamId?: string,
    eventId?: string,
    messageType?: string
  ) => void;
  isConnected: boolean;
  className?: string;
}

export function ChatInterface({
  title,
  channelType,
  channelId,
  messages,
  onSendMessage,
  isConnected,
  className = '',
}: ChatInterfaceProps) {
  const { user } = useAuth();
  const [newMessage, setNewMessage] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSendMessage = (e: React.FormEvent) => {
    e.preventDefault();
    if (!newMessage.trim() || !isConnected) return;

    onSendMessage(
      newMessage.trim(),
      channelType,
      channelType === 'direct' ? channelId : undefined,
      channelType === 'team' ? channelId : undefined,
      channelType === 'event' ? channelId : undefined,
      'text'
    );

    setNewMessage('');
    setIsTyping(false);
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setNewMessage(e.target.value);
    if (!isTyping) {
      setIsTyping(true);
      // Send typing indicator
      // TODO: Implement typing indicator via WebSocket
    }
  };

  const formatMessageTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  // Mock messages for demonstration
  const sampleMessages = [
    {
      id: '1',
      senderId: 'user1',
      senderName: 'Alex Chen',
      senderAvatar: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?w=40&h=40&fit=crop&crop=face',
      content: 'Hey everyone! Excited to start working on this hackathon project ðŸš€',
      timestamp: new Date(Date.now() - 1800000).toISOString(),
      type: 'text',
    },
    {
      id: '2',
      senderId: 'user2',
      senderName: 'Sarah Kim',
      senderAvatar: 'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?w=40&h=40&fit=crop&crop=face',
      content: 'Same here! I have some ideas for the UI design. Should we set up a quick call?',
      timestamp: new Date(Date.now() - 1500000).toISOString(),
      type: 'text',
    },
    {
      id: '3',
      senderId: 'user3',
      senderName: 'Mike Rodriguez',
      senderAvatar: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=40&h=40&fit=crop&crop=face',
      content: 'Perfect timing! I just pushed the initial backend setup to the repo.',
      timestamp: new Date(Date.now() - 900000).toISOString(),
      type: 'text',
    },
    {
      id: '4',
      senderId: user?.id || 'current-user',
      senderName: `${user?.firstName} ${user?.lastName}`,
      senderAvatar: user?.profileImageUrl,
      content: 'Awesome work everyone! Let me know if you need help with the frontend integration.',
      timestamp: new Date(Date.now() - 300000).toISOString(),
      type: 'text',
    },
  ];

  const displayMessages = messages.length > 0 ? messages : sampleMessages;

  return (
    <Card className={`glass-card flex flex-col h-96 ${className}`} data-testid="chat-interface">
      <CardHeader className="pb-3">
        <div className="flex items-center justify-between">
          <div>
            <CardTitle className="flex items-center gap-2 text-lg">
              ðŸ’¬ {title}
              <div className="flex items-center gap-1 text-sm font-normal">
                {isConnected ? (
                  <>
                    <Wifi className="w-3 h-3 text-chart-4" />
                    <span className="text-chart-4">Connected</span>
                  </>
                ) : (
                  <>
                    <WifiOff className="w-3 h-3 text-destructive" />
                    <span className="text-destructive">Disconnected</span>
                  </>
                )}
              </div>
            </CardTitle>
            <CardDescription>Real-time messaging â€¢ {displayMessages.length} messages</CardDescription>
          </div>
          <div className="flex items-center gap-2">
            <Badge variant="outline" className="text-xs">
              {channelType}
            </Badge>
            <Button variant="ghost" size="icon" className="h-8 w-8" data-testid="button-chat-options">
              <MoreHorizontal className="h-4 w-4" />
            </Button>
          </div>
        </div>
      </CardHeader>

      <Separator />

      {/* Messages Area */}
      <CardContent className="flex-1 p-4 overflow-y-auto scrollbar-thin space-y-4">
        {displayMessages.map((message, index) => {
          const isCurrentUser = message.senderId === user?.id;
          const showAvatar = index === 0 || displayMessages[index - 1]?.senderId !== message.senderId;

          return (
            <div
              key={message.id || index}
              className={`flex gap-3 ${isCurrentUser ? 'flex-row-reverse' : 'flex-row'}`}
              data-testid={`message-${index}`}
            >
              {showAvatar && !isCurrentUser && (
                <Avatar className="w-8 h-8 flex-shrink-0">
                  <AvatarImage src={message.senderAvatar} alt={message.senderName} />
                  <AvatarFallback className="text-xs">
                    {message.senderName?.split(' ').map(n => n[0]).join('') || '?'}
                  </AvatarFallback>
                </Avatar>
              )}

              {!showAvatar && !isCurrentUser && <div className="w-8" />}

              <div className={`flex-1 max-w-[80%] ${isCurrentUser ? 'flex flex-col items-end' : ''}`}>
                {showAvatar && (
                  <div className={`flex items-center gap-2 mb-1 ${isCurrentUser ? 'flex-row-reverse' : ''}`}>
                    <span className="text-sm font-medium">{message.senderName}</span>
                    <span className="text-xs text-muted-foreground">
                      {formatMessageTime(message.timestamp)}
                    </span>
                  </div>
                )}

                <div
                  className={`rounded-lg p-3 max-w-full break-words ${
                    isCurrentUser
                      ? 'bg-primary text-primary-foreground'
                      : 'bg-secondary text-secondary-foreground'
                  }`}
                >
                  <p className="text-sm leading-relaxed">{message.content}</p>
                </div>
              </div>
            </div>
          );
        })}

        {/* Typing Indicator */}
        {isTyping && (
          <div className="flex items-center gap-3">
            <div className="flex space-x-1">
              <div className="w-2 h-2 bg-muted-foreground rounded-full animate-pulse"></div>
              <div className="w-2 h-2 bg-muted-foreground rounded-full animate-pulse" style={{ animationDelay: '0.1s' }}></div>
              <div className="w-2 h-2 bg-muted-foreground rounded-full animate-pulse" style={{ animationDelay: '0.2s' }}></div>
            </div>
            <span className="text-sm text-muted-foreground">Someone is typing...</span>
          </div>
        )}

        <div ref={messagesEndRef} />
      </CardContent>

      <Separator />

      {/* Message Input */}
      <div className="p-4">
        <form onSubmit={handleSendMessage} className="flex items-center gap-2">
          <Button
            type="button"
            variant="ghost"
            size="icon"
            className="flex-shrink-0"
            data-testid="button-attach-file"
          >
            <Paperclip className="h-4 w-4" />
          </Button>

          <div className="flex-1 relative">
            <Input
              ref={inputRef}
              value={newMessage}
              onChange={handleInputChange}
              placeholder={
                isConnected 
                  ? "Type your message..." 
                  : "Connecting to chat..."
              }
              className="pr-12"
              disabled={!isConnected}
              data-testid="input-chat-message"
            />
            <Button
              type="button"
              variant="ghost"
              size="icon"
              className="absolute right-1 top-1/2 -translate-y-1/2 h-8 w-8"
              data-testid="button-emoji"
            >
              <Smile className="h-4 w-4" />
            </Button>
          </div>

          <Button
            type="submit"
            disabled={!newMessage.trim() || !isConnected}
            className="flex-shrink-0"
            data-testid="button-send-message"
          >
            <Send className="h-4 w-4" />
          </Button>
        </form>

        <div className="flex justify-between items-center mt-2 text-xs text-muted-foreground">
          <span>
            Press Enter to send â€¢ Shift+Enter for new line
          </span>
          <span className="flex items-center gap-1">
            {isConnected ? (
              <>
                <div className="w-2 h-2 bg-chart-4 rounded-full"></div>
                Online
              </>
            ) : (
              <>
                <div className="w-2 h-2 bg-destructive rounded-full"></div>
                Reconnecting...
              </>
            )}
          </span>
        </div>
      </div>
    </Card>
  );
}

client/src/components/EventBuilder.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Switch } from "@/components/ui/switch";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { insertEventSchema } from "@shared/schema";
import { z } from "zod";
import { 
  Wand2, 
  Palette, 
  Eye, 
  Save, 
  Code, 
  Users, 
  Trophy, 
  Calendar,
  MapPin,
  DollarSign,
  Tag,
  Upload,
  Settings,
  Trash2,
  Copy,
  Layout,
  Type,
  Image,
  Video
} from "lucide-react";

const eventBuilderSchema = insertEventSchema.extend({
  tags: z.array(z.string()).optional(),
  requirements: z.array(z.string()).optional(),
});

type EventBuilderForm = z.infer<typeof eventBuilderSchema>;

interface BuilderComponent {
  id: string;
  type: 'header' | 'text' | 'image' | 'video' | 'form' | 'schedule' | 'speakers';
  title: string;
  content?: any;
  order: number;
}

export default function EventBuilder() {
  const [activeTab, setActiveTab] = useState("details");
  const [selectedTemplate, setSelectedTemplate] = useState<string | null>(null);
  const [components, setComponents] = useState<BuilderComponent[]>([]);
  const [previewMode, setPreviewMode] = useState(false);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const form = useForm<EventBuilderForm>({
    resolver: zodResolver(eventBuilderSchema),
    defaultValues: {
      title: "",
      description: "",
      type: "hackathon",
      startDate: new Date(),
      endDate: new Date(),
      location: "",
      isVirtual: false,
      maxParticipants: 100,
      registrationFee: "0",
      prizePool: "0",
      tags: [],
      requirements: [],
    },
  });

  const createEventMutation = useMutation({
    mutationFn: async (data: EventBuilderForm) => {
      await apiRequest("POST", "/api/events", data);
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Event created successfully!",
      });
      queryClient.invalidateQueries({ queryKey: ["/api/events"] });
      form.reset();
      setComponents([]);
    },
    onError: (error: Error) => {
      toast({
        title: "Error",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const generateAIContentMutation = useMutation({
    mutationFn: async (eventType: string) => {
      // This would call the AI content generation API
      await new Promise(resolve => setTimeout(resolve, 2000));
      return {
        title: `AI-Generated ${eventType} Event`,
        description: `An exciting ${eventType} event designed to bring together innovators and creators.`,
        schedule: [
          { time: "09:00", activity: "Registration & Welcome", duration: "1 hour" },
          { time: "10:00", activity: "Opening Keynote", duration: "1 hour" },
          { time: "11:00", activity: "Main Activity", duration: "6 hours" },
          { time: "17:00", activity: "Closing Ceremony", duration: "1 hour" },
        ],
        requirements: ["Laptop", "Enthusiasm", "Team spirit"],
        tags: [eventType, "Innovation", "Technology"],
      };
    },
    onSuccess: (data) => {
      form.setValue("title", data.title);
      form.setValue("description", data.description);
      form.setValue("requirements", data.requirements);
      form.setValue("tags", data.tags);
      toast({
        title: "AI Content Generated",
        description: "Event content has been generated successfully!",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to generate AI content",
        variant: "destructive",
      });
    },
  });

  const eventTemplates = [
    {
      id: "hackathon",
      name: "Hackathon Template",
      icon: <Code className="h-6 w-6" />,
      description: "Perfect for coding competitions and innovation challenges",
      color: "bg-primary/20 text-primary border-primary/30"
    },
    {
      id: "workshop",
      name: "Workshop Template",
      icon: <Users className="h-6 w-6" />,
      description: "Interactive learning sessions and skill development",
      color: "bg-accent/20 text-accent border-accent/30"
    },
    {
      id: "conference",
      name: "Conference Template",
      icon: <Calendar className="h-6 w-6" />,
      description: "Professional conferences and networking events",
      color: "bg-chart-3/20 text-chart-3 border-chart-3/30"
    },
    {
      id: "competition",
      name: "Competition Template",
      icon: <Trophy className="h-6 w-6" />,
      description: "Competitive events with prizes and recognition",
      color: "bg-chart-5/20 text-chart-5 border-chart-5/30"
    }
  ];

  const availableComponents = [
    { type: 'header', title: 'Header Section', icon: <Layout className="h-5 w-5" /> },
    { type: 'text', title: 'Text Block', icon: <Type className="h-5 w-5" /> },
    { type: 'image', title: 'Image Gallery', icon: <Image className="h-5 w-5" /> },
    { type: 'video', title: 'Video Player', icon: <Video className="h-5 w-5" /> },
    { type: 'form', title: 'Registration Form', icon: <Users className="h-5 w-5" /> },
    { type: 'schedule', title: 'Event Schedule', icon: <Calendar className="h-5 w-5" /> },
    { type: 'speakers', title: 'Speaker List', icon: <Users className="h-5 w-5" /> },
  ];

  const addComponent = (type: string) => {
    const newComponent: BuilderComponent = {
      id: Date.now().toString(),
      type: type as any,
      title: `New ${type}`,
      order: components.length,
    };
    setComponents([...components, newComponent]);
  };

  const removeComponent = (id: string) => {
    setComponents(components.filter(comp => comp.id !== id));
  };

  const moveComponent = (id: string, direction: 'up' | 'down') => {
    const index = components.findIndex(comp => comp.id === id);
    if (
      (direction === 'up' && index > 0) ||
      (direction === 'down' && index < components.length - 1)
    ) {
      const newComponents = [...components];
      const targetIndex = direction === 'up' ? index - 1 : index + 1;
      [newComponents[index], newComponents[targetIndex]] = [newComponents[targetIndex], newComponents[index]];
      setComponents(newComponents);
    }
  };

  const onSubmit = (data: EventBuilderForm) => {
    createEventMutation.mutate(data);
  };

  if (previewMode) {
    return (
      <div className="space-y-8">
        <div className="flex items-center justify-between">
          <h2 className="text-2xl font-bold">Event Preview</h2>
          <Button onClick={() => setPreviewMode(false)} variant="outline">
            <Settings className="h-4 w-4 mr-2" />
            Edit Mode
          </Button>
        </div>

        <Card>
          <CardContent className="p-8">
            <div className="space-y-8">
              <div className="text-center">
                <h1 className="text-4xl font-bold mb-4">{form.watch("title") || "Event Title"}</h1>
                <p className="text-xl text-muted-foreground">{form.watch("description") || "Event description"}</p>
              </div>

              {components.map((component) => (
                <div key={component.id} className="border border-border rounded-lg p-6">
                  <h3 className="font-semibold mb-2">{component.title}</h3>
                  <p className="text-sm text-muted-foreground">
                    {component.type} component preview would render here
                  </p>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    );
  }

  return (
    <div className="space-y-8">
      <div className="flex items-center justify-between">
        <h2 className="text-2xl font-bold">AI-Powered Event Builder</h2>
        <div className="flex items-center gap-2">
          <Button onClick={() => setPreviewMode(true)} variant="outline">
            <Eye className="h-4 w-4 mr-2" />
            Preview
          </Button>
          <Button
            onClick={() => generateAIContentMutation.mutate(form.watch("type"))}
            disabled={generateAIContentMutation.isPending}
            className="bg-accent text-accent-foreground hover:bg-accent/90"
          >
            {generateAIContentMutation.isPending ? (
              <div className="animate-spin w-4 h-4 mr-2 border-2 border-current border-t-transparent rounded-full" />
            ) : (
              <Wand2 className="h-4 w-4 mr-2" />
            )}
            AI Generate
          </Button>
        </div>
      </div>

      <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
        <TabsList className="grid w-full grid-cols-4">
          <TabsTrigger value="templates">Templates</TabsTrigger>
          <TabsTrigger value="details">Event Details</TabsTrigger>
          <TabsTrigger value="builder">Page Builder</TabsTrigger>
          <TabsTrigger value="settings">Settings</TabsTrigger>
        </TabsList>

        <TabsContent value="templates" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Choose a Template</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid md:grid-cols-2 gap-6">
                {eventTemplates.map((template) => (
                  <Card
                    key={template.id}
                    className={`cursor-pointer transition-all hover:scale-105 ${
                      selectedTemplate === template.id
                        ? 'ring-2 ring-primary bg-primary/5'
                        : 'hover:bg-muted/50'
                    }`}
                    onClick={() => {
                      setSelectedTemplate(template.id);
                      form.setValue("type", template.id as any);
                    }}
                  >
                    <CardContent className="p-6">
                      <div className="flex items-center gap-4 mb-4">
                        <div className={`w-12 h-12 rounded-lg ${template.color} flex items-center justify-center`}>
                          {template.icon}
                        </div>
                        <div>
                          <h3 className="font-semibold">{template.name}</h3>
                          <p className="text-sm text-muted-foreground">{template.description}</p>
                        </div>
                      </div>
                      {selectedTemplate === template.id && (
                        <Badge className="bg-primary text-primary-foreground">Selected</Badge>
                      )}
                    </CardContent>
                  </Card>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="details" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle>Event Information</CardTitle>
            </CardHeader>
            <CardContent>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                  <div className="grid md:grid-cols-2 gap-6">
                    <FormField
                      control={form.control}
                      name="title"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Event Title</FormLabel>
                          <FormControl>
                            <Input placeholder="Enter event title..." {...field} data-testid="input-event-title" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="type"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Event Type</FormLabel>
                          <Select onValueChange={field.onChange} defaultValue={field.value}>
                            <FormControl>
                              <SelectTrigger data-testid="select-event-type">
                                <SelectValue placeholder="Select event type" />
                              </SelectTrigger>
                            </FormControl>
                            <SelectContent>
                              <SelectItem value="hackathon">Hackathon</SelectItem>
                              <SelectItem value="workshop">Workshop</SelectItem>
                              <SelectItem value="quiz">Quiz</SelectItem>
                              <SelectItem value="conference">Conference</SelectItem>
                            </SelectContent>
                          </Select>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <FormField
                    control={form.control}
                    name="description"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Description</FormLabel>
                        <FormControl>
                          <Textarea 
                            placeholder="Describe your event..." 
                            className="min-h-[100px]"
                            {...field}
                            data-testid="textarea-event-description"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <div className="grid md:grid-cols-2 gap-6">
                    <FormField
                      control={form.control}
                      name="startDate"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Start Date</FormLabel>
                          <FormControl>
                            <Input 
                              type="datetime-local" 
                              {...field}
                              value={field.value ? new Date(field.value).toISOString().slice(0, 16) : ''}
                              onChange={(e) => field.onChange(new Date(e.target.value))}
                              data-testid="input-start-date"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="endDate"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>End Date</FormLabel>
                          <FormControl>
                            <Input 
                              type="datetime-local" 
                              {...field}
                              value={field.value ? new Date(field.value).toISOString().slice(0, 16) : ''}
                              onChange={(e) => field.onChange(new Date(e.target.value))}
                              data-testid="input-end-date"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid md:grid-cols-2 gap-6">
                    <FormField
                      control={form.control}
                      name="location"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Location</FormLabel>
                          <FormControl>
                            <Input placeholder="Event location..." {...field} data-testid="input-location" />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="isVirtual"
                      render={({ field }) => (
                        <FormItem className="flex flex-row items-center justify-between rounded-lg border p-3 shadow-sm">
                          <div className="space-y-0.5">
                            <FormLabel>Virtual Event</FormLabel>
                          </div>
                          <FormControl>
                            <Switch
                              checked={field.value}
                              onCheckedChange={field.onChange}
                              data-testid="switch-virtual-event"
                            />
                          </FormControl>
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="grid md:grid-cols-3 gap-6">
                    <FormField
                      control={form.control}
                      name="maxParticipants"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Max Participants</FormLabel>
                          <FormControl>
                            <Input 
                              type="number" 
                              placeholder="100" 
                              {...field}
                              onChange={(e) => field.onChange(parseInt(e.target.value) || 0)}
                              data-testid="input-max-participants"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="registrationFee"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Registration Fee ($)</FormLabel>
                          <FormControl>
                            <Input 
                              type="number" 
                              placeholder="0.00" 
                              step="0.01"
                              {...field}
                              data-testid="input-registration-fee"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />

                    <FormField
                      control={form.control}
                      name="prizePool"
                      render={({ field }) => (
                        <FormItem>
                          <FormLabel>Prize Pool ($)</FormLabel>
                          <FormControl>
                            <Input 
                              type="number" 
                              placeholder="0.00" 
                              step="0.01"
                              {...field}
                              data-testid="input-prize-pool"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  </div>

                  <div className="flex justify-end gap-4">
                    <Button type="button" variant="outline" onClick={() => form.reset()}>
                      Reset
                    </Button>
                    <Button 
                      type="submit" 
                      disabled={createEventMutation.isPending}
                      className="bg-primary text-primary-foreground hover:bg-primary/90"
                      data-testid="button-create-event"
                    >
                      {createEventMutation.isPending ? (
                        <div className="animate-spin w-4 h-4 mr-2 border-2 border-current border-t-transparent rounded-full" />
                      ) : (
                        <Save className="h-4 w-4 mr-2" />
                      )}
                      Create Event
                    </Button>
                  </div>
                </form>
              </Form>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="builder" className="space-y-6">
          <div className="grid lg:grid-cols-4 gap-6">
            {/* Component Library */}
            <Card>
              <CardHeader>
                <CardTitle className="text-lg">Components</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-2">
                  {availableComponents.map((component) => (
                    <Button
                      key={component.type}
                      variant="outline"
                      className="w-full justify-start"
                      onClick={() => addComponent(component.type)}
                      data-testid={`button-add-${component.type}`}
                    >
                      {component.icon}
                      <span className="ml-2">{component.title}</span>
                    </Button>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Builder Canvas */}
            <Card className="lg:col-span-3">
              <CardHeader>
                <CardTitle className="text-lg">Page Builder</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="min-h-96 border-2 border-dashed border-border rounded-xl p-6">
                  {components.length === 0 ? (
                    <div className="text-center text-muted-foreground">
                      <Layout className="h-16 w-16 mx-auto mb-4" />
                      <p>Drag components here to build your event page</p>
                    </div>
                  ) : (
                    <div className="space-y-4">
                      {components.map((component, index) => (
                        <div
                          key={component.id}
                          className="p-4 bg-card rounded-lg border border-border group hover:border-primary/50 transition-colors"
                        >
                          <div className="flex items-center justify-between mb-2">
                            <h5 className="font-semibold">{component.title}</h5>
                            <div className="flex items-center gap-2 opacity-0 group-hover:opacity-100 transition-opacity">
                              <Button size="sm" variant="ghost" onClick={() => moveComponent(component.id, 'up')} disabled={index === 0}>
                                â†‘
                              </Button>
                              <Button size="sm" variant="ghost" onClick={() => moveComponent(component.id, 'down')} disabled={index === components.length - 1}>
                                â†“
                              </Button>
                              <Button size="sm" variant="ghost">
                                <Copy className="h-4 w-4" />
