import { useState } from "react";
import { Link, useLocation } from "wouter";
import { useAuth } from "@/hooks/useAuth";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Bell, MessageSquare, Menu, X } from "lucide-react";

export default function Header() {
  const { user, isAuthenticated } = useAuth();
  const [location] = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const navigation = [
    { name: "Dashboard", href: "/" },
    { name: "Events", href: "/events" },
    { name: "Community", href: "/community" },
    { name: "Analytics", href: "/analytics" },
    { name: "Virtual Space", href: "/virtual-space" },
  ];

  return (
    <header className="fixed w-full top-0 left-0 z-50 glass-card">
      <div className="container mx-auto px-6 py-4">
        <div className="flex justify-between items-center">
          <Link href={isAuthenticated ? "/" : "/"}>
            <a className="flex items-center space-x-3">
              <div className="w-10 h-10 rounded-xl bg-gradient-to-br from-primary to-accent flex items-center justify-center">
                <svg className="w-6 h-6 text-white" viewBox="0 0 24 24" fill="currentColor">
                  <path d="M12 2L13.09 8.26L22 9L13.09 9.74L12 16L10.91 9.74L2 9L10.91 8.26L12 2Z" />
                </svg>
              </div>
              <h1 className="text-2xl font-bold gradient-text">Nexus</h1>
            </a>
          </Link>

          {isAuthenticated && (
            <nav className="hidden lg:flex items-center space-x-8">
              {navigation.map((item) => (
                <Link key={item.name} href={item.href}>
                  <a className={`text-sm font-medium transition-colors ${
                    location === item.href 
                      ? "text-primary" 
                      : "text-muted-foreground hover:text-foreground"
                  }`}>
                    {item.name}
                  </a>
                </Link>
              ))}
            </nav>
          )}

          <div className="flex items-center space-x-4">
            {!isAuthenticated ? (
              <div className="flex items-center space-x-3">
                <Button
                  onClick={() => window.location.href = "/api/login"}
                  className="bg-primary text-primary-foreground hover:bg-primary/90"
                  data-testid="login-button"
                >
                  Sign In
                </Button>
              </div>
            ) : (
              <>
                {/* Notifications */}
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" size="sm" className="relative">
                      <Bell className="h-5 w-5" />
                      <span className="notification-dot" />
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end" className="w-80">
                    <div className="p-4 border-b border-border">
                      <h3 className="font-semibold">Notifications</h3>
                    </div>
                    <div className="p-2">
                      <DropdownMenuItem>
                        <div className="flex items-start space-x-3">
                          <div className="w-8 h-8 bg-primary rounded-full flex items-center justify-center">
                            <Bell className="h-4 w-4 text-primary-foreground" />
                          </div>
                          <div>
                            <p className="text-sm font-medium">AI Hackathon starting soon</p>
                            <p className="text-xs text-muted-foreground">Event begins in 30 minutes</p>
                          </div>
                        </div>
                      </DropdownMenuItem>
                    </div>
                  </DropdownMenuContent>
                </DropdownMenu>

                {/* Messages */}
                <Button variant="ghost" size="sm" className="relative">
                  <MessageSquare className="h-5 w-5" />
                  <span className="notification-dot" />
                </Button>

                {/* User Profile */}
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant="ghost" className="flex items-center space-x-2">
                      <Avatar className="h-8 w-8">
                        <AvatarImage src={user?.profileImageUrl || ""} />
                        <AvatarFallback>
                          {user?.firstName?.[0]}{user?.lastName?.[0]}
                        </AvatarFallback>
                      </Avatar>
                      <div className="hidden md:block text-left">
                        <p className="text-sm font-medium">
                          {user?.firstName} {user?.lastName}
                        </p>
                        <p className="text-xs text-muted-foreground">Organizer</p>
                      </div>
                    </Button>
                  </DropdownMenuTrigger>
                  <DropdownMenuContent align="end">
                    <DropdownMenuItem>Profile</DropdownMenuItem>
                    <DropdownMenuItem>Settings</DropdownMenuItem>
                    <DropdownMenuItem>
                      <a href="/api/logout" className="w-full">Sign Out</a>
                    </DropdownMenuItem>
                  </DropdownMenuContent>
                </DropdownMenu>

                {/* Mobile Menu Button */}
                <Button
                  variant="ghost"
                  size="sm"
                  className="lg:hidden"
                  onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
                >
                  {mobileMenuOpen ? <X className="h-5 w-5" /> : <Menu className="h-5 w-5" />}
                </Button>
              </>
            )}
          </div>
        </div>

        {/* Mobile Navigation */}
        {isAuthenticated && mobileMenuOpen && (
          <div className="lg:hidden mt-4 pt-4 border-t border-border">
            <nav className="flex flex-col space-y-2">
              {navigation.map((item) => (
                <Link key={item.name} href={item.href}>
                  <a 
                    className={`block px-3 py-2 text-sm font-medium transition-colors ${
                      location === item.href 
                        ? "text-primary bg-primary/10" 
                        : "text-muted-foreground hover:text-foreground hover:bg-muted"
                    }`}
                    onClick={() => setMobileMenuOpen(false)}
                  >
                    {item.name}
                  </a>
                </Link>
              ))}
            </nav>
          </div>
        )}
      </div>
    </header>
  );
}

client/src/components/PaymentForm.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { Badge } from "@/components/ui/badge";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { z } from "zod";
import { 
  CreditCard, 
  Lock, 
  Shield, 
  DollarSign,
  Calendar,
  User,
  MapPin,
  Check,
  X,
  AlertCircle
} from "lucide-react";

const paymentSchema = z.object({
  email: z.string().email("Invalid email address"),
  cardNumber: z.string().min(16, "Card number must be 16 digits").max(19, "Invalid card number"),
  expiryMonth: z.string().min(2, "Required").max(2, "Invalid month"),
  expiryYear: z.string().min(2, "Required").max(2, "Invalid year"),
  cvc: z.string().min(3, "CVC must be 3-4 digits").max(4, "Invalid CVC"),
  cardholderName: z.string().min(2, "Cardholder name required"),
  billingAddress: z.string().min(5, "Billing address required"),
  city: z.string().min(2, "City required"),
  zipCode: z.string().min(5, "ZIP code required"),
  saveCard: z.boolean().optional(),
  amount: z.number().min(0.01, "Amount must be greater than 0"),
  eventId: z.string().optional(),
});

type PaymentFormData = z.infer<typeof paymentSchema>;

interface PaymentFormProps {
  initialAmount?: number;
  eventId?: string;
  eventTitle?: string;
  onSuccess?: () => void;
  onCancel?: () => void;
}

export default function PaymentForm({ 
  initialAmount = 99, 
  eventId,
  eventTitle = "Event Registration",
  onSuccess,
  onCancel 
}: PaymentFormProps) {
  const [step, setStep] = useState<'payment' | 'processing' | 'success' | 'error'>('payment');
  const [clientSecret, setClientSecret] = useState<string | null>(null);
  const { toast } = useToast();

  const form = useForm<PaymentFormData>({
    resolver: zodResolver(paymentSchema),
    defaultValues: {
      email: "",
      cardNumber: "",
      expiryMonth: "",
      expiryYear: "",
      cvc: "",
      cardholderName: "",
      billingAddress: "",
      city: "",
      zipCode: "",
      saveCard: false,
      amount: initialAmount,
      eventId: eventId,
    },
  });

  // Create payment intent
  const createPaymentIntentMutation = useMutation({
    mutationFn: async (amount: number) => {
      const response = await apiRequest("POST", "/api/create-payment-intent", { 
        amount,
        eventId 
      });
      return await response.json();
    },
    onSuccess: (data) => {
      setClientSecret(data.clientSecret);
    },
    onError: (error: Error) => {
      toast({
        title: "Payment Setup Failed",
        description: error.message,
        variant: "destructive",
      });
      setStep('error');
    },
  });

  // Process payment
  const processPaymentMutation = useMutation({
    mutationFn: async (data: PaymentFormData) => {
      setStep('processing');

      // First create payment intent if not already created
      if (!clientSecret) {
        const intentResponse = await apiRequest("POST", "/api/create-payment-intent", { 
          amount: data.amount,
          eventId: data.eventId 
        });
        const { clientSecret: newClientSecret } = await intentResponse.json();
        setClientSecret(newClientSecret);
      }

      // Simulate payment processing
      await new Promise(resolve => setTimeout(resolve, 3000));

      // In a real implementation, this would use Stripe's confirmPayment
      return { success: true };
    },
    onSuccess: () => {
      setStep('success');
      if (onSuccess) {
        setTimeout(onSuccess, 2000);
      }
    },
    onError: (error: Error) => {
      setStep('error');
      toast({
        title: "Payment Failed",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: PaymentFormData) => {
    processPaymentMutation.mutate(data);
  };

  const formatCardNumber = (value: string) => {
    const v = value.replace(/\s+/g, '').replace(/[^0-9]/gi, '');
    const matches = v.match(/\d{4,16}/g);
    const match = matches && matches[0] || '';
    const parts = [];
    for (let i = 0, len = match.length; i < len; i += 4) {
      parts.push(match.substring(i, i + 4));
    }
    if (parts.length) {
      return parts.join(' ');
    } else {
      return v;
    }
  };

  const getCardType = (number: string) => {
    const cleanNumber = number.replace(/\s/g, '');
    if (cleanNumber.startsWith('4')) return 'visa';
    if (cleanNumber.startsWith('5') || cleanNumber.startsWith('2')) return 'mastercard';
    if (cleanNumber.startsWith('3')) return 'amex';
    return 'unknown';
  };

  const cardType = getCardType(form.watch('cardNumber') || '');

  if (step === 'processing') {
    return (
      <Card className="max-w-md mx-auto">
        <CardContent className="p-8 text-center">
          <div className="w-16 h-16 bg-primary/20 rounded-full flex items-center justify-center mx-auto mb-4">
            <div className="animate-spin w-8 h-8 border-4 border-primary border-t-transparent rounded-full" />
          </div>
          <h3 className="text-lg font-semibold mb-2">Processing Payment</h3>
          <p className="text-muted-foreground">Please wait while we process your payment securely...</p>
        </CardContent>
      </Card>
    );
  }

  if (step === 'success') {
    return (
      <Card className="max-w-md mx-auto">
        <CardContent className="p-8 text-center">
          <div className="w-16 h-16 bg-chart-4/20 rounded-full flex items-center justify-center mx-auto mb-4">
            <Check className="w-8 h-8 text-chart-4" />
          </div>
          <h3 className="text-lg font-semibold mb-2 text-chart-4">Payment Successful!</h3>
          <p className="text-muted-foreground mb-4">
            Thank you for your payment. You will receive a confirmation email shortly.
          </p>
          <div className="text-sm text-muted-foreground">
            <p>Amount: ${initialAmount}</p>
            <p>Event: {eventTitle}</p>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (step === 'error') {
    return (
      <Card className="max-w-md mx-auto">
        <CardContent className="p-8 text-center">
          <div className="w-16 h-16 bg-red-500/20 rounded-full flex items-center justify-center mx-auto mb-4">
            <X className="w-8 h-8 text-red-500" />
          </div>
          <h3 className="text-lg font-semibold mb-2 text-red-500">Payment Failed</h3>
          <p className="text-muted-foreground mb-4">
            There was an error processing your payment. Please try again.
          </p>
          <div className="flex gap-2">
            <Button variant="outline" onClick={() => setStep('payment')} className="flex-1">
              Try Again
            </Button>
            {onCancel && (
              <Button variant="ghost" onClick={onCancel} className="flex-1">
                Cancel
              </Button>
            )}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="max-w-2xl mx-auto">
      <div className="grid lg:grid-cols-2 gap-8">
        {/* Payment Form */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <CreditCard className="h-5 w-5" />
              Payment Information
            </CardTitle>
          </CardHeader>
          <CardContent>
            <Form {...form}>
              <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                <FormField
                  control={form.control}
                  name="email"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Email Address</FormLabel>
                      <FormControl>
                        <Input 
                          type="email" 
                          placeholder="john@example.com" 
                          {...field}
                          data-testid="input-payment-email"
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="cardNumber"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Card Number</FormLabel>
                      <FormControl>
                        <div className="relative">
                          <Input 
                            placeholder="1234 5678 9012 3456"
                            {...field}
                            onChange={(e) => field.onChange(formatCardNumber(e.target.value))}
                            maxLength={19}
                            data-testid="input-card-number"
                          />
                          <div className="absolute right-3 top-3 flex space-x-1">
                            {cardType === 'visa' && (
                              <Badge variant="outline" className="text-blue-600">VISA</Badge>
                            )}
                            {cardType === 'mastercard' && (
                              <Badge variant="outline" className="text-red-600">MC</Badge>
                            )}
                            {cardType === 'amex' && (
                              <Badge variant="outline" className="text-green-600">AMEX</Badge>
                            )}
                          </div>
                        </div>
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <div className="grid grid-cols-3 gap-4">
                  <FormField
                    control={form.control}
                    name="expiryMonth"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Month</FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger data-testid="select-expiry-month">
                              <SelectValue placeholder="MM" />
                            </SelectTrigger>
                          </FormControl>
                          <SelectContent>
                            {Array.from({ length: 12 }, (_, i) => (
                              <SelectItem key={i + 1} value={String(i + 1).padStart(2, '0')}>
                                {String(i + 1).padStart(2, '0')}
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="expiryYear"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Year</FormLabel>
                        <Select onValueChange={field.onChange} defaultValue={field.value}>
                          <FormControl>
                            <SelectTrigger data-testid="select-expiry-year">
                              <SelectValue placeholder="YY" />
                            </SelectTrigger>
                          </FormControl>
