import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import PaymentForm from "@/components/PaymentForm";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/hooks/useAuth";
import { 
  CreditCard, 
  DollarSign, 
  TrendingUp, 
  Users, 
  Download,
  Plus,
  Building,
  Calendar,
  CheckCircle,
  Clock,
  XCircle
} from "lucide-react";

interface Transaction {
  id: string;
  type: 'registration' | 'sponsorship' | 'subscription';
  description: string;
  amount: number;
  status: 'completed' | 'pending' | 'failed';
  date: string;
  eventTitle?: string;
}

interface RevenueData {
  totalRevenue: number;
  monthlyRevenue: number;
  registrationFees: number;
  sponsorships: number;
  premiumFeatures: number;
}

interface Sponsor {
  id: string;
  name: string;
  tier: 'platinum' | 'gold' | 'silver';
  amount: number;
  status: 'paid' | 'pending' | 'overdue';
  logo?: string;
}

export default function Payments() {
  const [activeTab, setActiveTab] = useState("overview");
  const [showPaymentForm, setShowPaymentForm] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState<string | null>(null);
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Mock data - in production this would come from API
  const mockRevenueData: RevenueData = {
    totalRevenue: 248000,
    monthlyRevenue: 89000,
    registrationFees: 168000,
    sponsorships: 72000,
    premiumFeatures: 8000,
  };

  const mockTransactions: Transaction[] = [
    {
      id: "1",
      type: "registration",
      description: "AI Hackathon Registration",
      amount: 99,
      status: "completed",
      date: "2024-12-10T10:30:00Z",
      eventTitle: "AI Innovation Hackathon 2024"
    },
    {
      id: "2",
      type: "sponsorship",
      description: "Google Platinum Sponsorship",
      amount: 50000,
      status: "completed",
      date: "2024-12-09T14:20:00Z"
    },
    {
      id: "3",
      type: "registration",
      description: "React Workshop Registration",
      amount: 149,
      status: "pending",
      date: "2024-12-08T16:45:00Z",
      eventTitle: "React Masterclass"
    }
  ];

  const mockSponsors: Sponsor[] = [
    {
      id: "1",
      name: "Google",
      tier: "platinum",
      amount: 50000,
      status: "paid"
    },
    {
      id: "2",
      name: "Microsoft",
      tier: "gold",
      amount: 30000,
      status: "paid"
    },
    {
      id: "3",
      name: "Amazon",
      tier: "silver",
      amount: 15000,
      status: "pending"
    }
  ];

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
      case 'paid':
        return 'bg-chart-4/20 text-chart-4';
      case 'pending':
        return 'bg-chart-5/20 text-chart-5';
      case 'failed':
      case 'overdue':
        return 'bg-red-500/20 text-red-500';
      default:
        return 'bg-muted text-muted-foreground';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
      case 'paid':
        return <CheckCircle className="h-4 w-4" />;
      case 'pending':
        return <Clock className="h-4 w-4" />;
      case 'failed':
      case 'overdue':
        return <XCircle className="h-4 w-4" />;
      default:
        return null;
    }
  };

  const getTierColor = (tier: string) => {
    switch (tier) {
      case 'platinum':
        return 'bg-primary/20 text-primary border-primary/30';
      case 'gold':
        return 'bg-chart-5/20 text-chart-5 border-chart-5/30';
      case 'silver':
        return 'bg-muted text-muted-foreground border-border';
      default:
        return 'bg-muted text-muted-foreground border-border';
    }
  };

  const handlePaymentSuccess = () => {
    setShowPaymentForm(false);
    toast({
      title: "Payment Successful",
      description: "Your payment has been processed successfully!",
    });
    queryClient.invalidateQueries({ queryKey: ["/api/payments"] });
  };

  return (
    <div className="min-h-screen pt-20">
      <div className="container mx-auto px-6 py-8">
        {/* Header */}
        <div className="text-center mb-16">
          <h1 className="text-5xl font-bold mb-4">
            Secure <span className="gradient-text">Payments</span>
          </h1>
          <p className="text-xl text-muted-foreground max-w-3xl mx-auto">
            Integrated payment processing, sponsorship management, and financial analytics
          </p>
        </div>

        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
          <TabsList className="grid w-full grid-cols-4 mb-8">
            <TabsTrigger value="overview" className="flex items-center gap-2">
              <DollarSign className="h-4 w-4" />
              Overview
            </TabsTrigger>
            <TabsTrigger value="transactions" className="flex items-center gap-2">
              <CreditCard className="h-4 w-4" />
              Transactions
            </TabsTrigger>
            <TabsTrigger value="sponsors" className="flex items-center gap-2">
              <Building className="h-4 w-4" />
              Sponsors
            </TabsTrigger>
            <TabsTrigger value="analytics" className="flex items-center gap-2">
              <TrendingUp className="h-4 w-4" />
              Analytics
            </TabsTrigger>
          </TabsList>

          <TabsContent value="overview" className="space-y-8">
            {/* Revenue Overview */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
              <Card>
                <CardContent className="p-6">
                  <div className="flex items-center justify-between mb-4">
